// frontend/src/pages/AnimeDetail.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';

function Reviews({ animeId, user }) {
  const [reviews, setReviews] = useState([]);
  const [text, setText] = useState('');
  const [rating, setRating] = useState('');
  const [loading, setLoading] = useState(true);

  const load = async () => {
    const res = await api.get(`/reviews/anime/${animeId}`);
    setReviews(res.data);
    setLoading(false);
  };

  useEffect(()=>{ load() }, [animeId]);

  const submit = async () => {
    if(!user) return alert('Please login to review');
    await api.post('/reviews', { animeId, text, rating: rating ? Number(rating) : null });
    setText(''); setRating('');
    load();
  };

  const remove = async (id) => {
    await api.delete(`/reviews/${id}`);
    load();
  };

  if (loading) return <div>Loading reviews...</div>;
  return (
    <div style={{ marginTop:12 }}>
      <h4>Reviews</h4>
      {user && (
        <div className="card" style={{ marginBottom:12 }}>
          <textarea placeholder="Write your review..." value={text} onChange={e=>setText(e.target.value)} />
          <div style={{ display:'flex', gap:8, marginTop:8 }}>
            <input placeholder="rating 0-10" value={rating} onChange={e=>setRating(e.target.value)} style={{ width:150 }} />
            <button className="btn btn-primary" onClick={submit}>Save review</button>
          </div>
        </div>
      )}

      {reviews.length === 0 && <div className="small">No reviews yet</div>}
      {reviews.map(r => (
        <div key={r._id} className="card" style={{ marginBottom:8 }}>
          <div style={{ display:'flex', justifyContent:'space-between' }}>
            <div><strong>{r.username || r.user?.username}</strong> <span className="small">• {new Date(r.createdAt).toLocaleString()}</span></div>
            <div className="small">Rating: {r.rating ?? '—'}</div>
          </div>
          <div style={{ marginTop:8 }}>{r.text}</div>
          {user && r.user && r.user._id === user.id && (
            <div style={{ marginTop:8 }}>
              <button className="btn" onClick={()=>remove(r._id)}>Delete</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default function AnimeDetail({ user }) {
  const { id } = useParams();
  const [anime, setAnime] = useState(null);
  const [status, setStatus] = useState('plan-to-watch');
  const [score, setScore] = useState('');
  const [msg, setMsg] = useState('');

  useEffect(() => {
    api.get(`/anime/${id}`).then(r => setAnime(r.data)).catch(()=>{});
  }, [id]);

  const add = async () => {
    try {
      await api.post('/users/me/list', { animeId: id, status, score: score ? Number(score) : undefined });
      setMsg('Updated list');
    } catch (err) {
      setMsg(err.response?.data?.msg || 'Action failed');
    }
  };

  if (!anime) return <div>Loading...</div>;

  return (
    <div style={{ display:'flex', gap:16 }}>
      <img src={anime.image_url} alt={anime.title} style={{ width:300 }} />
      <div style={{ flex:1 }}>
        <h2>{anime.title}</h2>
        <div className="small">Score: {anime.score}</div>
        <p style={{ marginTop:8 }}>{anime.synopsis}</p>

        {anime.streaming_links && anime.streaming_links.length > 0 && (
          <div style={{ marginTop:12 }}>
            <h4>Watch on</h4>
            <div style={{ display:'flex', gap:8, flexWrap:'wrap' }}>
              {anime.streaming_links.map((s, i) => (
                <a key={i} href={s.url} target="_blank" rel="noreferrer" className="btn">{s.name}</a>
              ))}
            </div>
          </div>
        )}

        {user ? (
          <div style={{ marginTop:12 }}>
            <h4>Add/update to your list</h4>
            <select value={status} onChange={e=>setStatus(e.target.value)}>
              <option value="watching">watching</option>
              <option value="completed">completed</option>
              <option value="on-hold">on-hold</option>
              <option value="dropped">dropped</option>
              <option value="plan-to-watch">plan-to-watch</option>
            </select>
            <input placeholder="score 0-10" value={score} onChange={e=>setScore(e.target.value)} />
            <button className="btn btn-primary" onClick={add}>Save</button>
            {msg && <div style={{ marginTop:8 }}>{msg}</div>}
          </div>
        ) : <div className="small">Please login to add to list</div>}

        <Reviews animeId={id} user={user} />
      </div>
    </div>
  );
}
