// frontend/src/pages/ClubDetail.js
import React, { useEffect, useState } from 'react';
import api from '../api';
import { useParams } from 'react-router-dom';

export default function ClubDetail({ user }) {
  const { id } = useParams();
  const [club, setClub] = useState(null);
  const [content, setContent] = useState('');

  const load = async () => {
    const res = await api.get(`/clubs/${id}`);
    setClub(res.data);
  };

  useEffect(()=>{ load() }, [id]);

  const join = async () => { await api.post(`/clubs/${id}/join`); load(); };
  const leave = async () => { await api.post(`/clubs/${id}/leave`); load(); };
  const post = async () => { await api.post(`/clubs/${id}/posts`, { content }); setContent(''); load(); };

  if(!club) return <div>Loading...</div>;
  const isMember = club.members.some(m => m._id === user?.id);
  return (
    <div>
      <h2>{club.name}</h2>
      <div className="small">{club.description}</div>
      <div style={{ marginTop:12 }}>
        {user ? (isMember ? <button className="btn" onClick={leave}>Leave</button> : <button className="btn" onClick={join}>Join</button>) : <div className="small">Log in to join</div>}
      </div>

      <div style={{ marginTop:16 }}>
        <h4>Discussion</h4>
        {user && (
          <div className="card" style={{ marginBottom:12 }}>
            <textarea value={content} onChange={e=>setContent(e.target.value)} placeholder="Write a post..." />
            <button className="btn btn-primary" onClick={post} style={{ marginTop:8 }}>Post</button>
          </div>
        )}

        {club.posts && club.posts.length === 0 && <div className="small">No posts yet</div>}
        {club.posts && club.posts.map(p => (
          <div key={p._id} className="card" style={{ marginBottom:8 }}>
            <div><strong>{p.username}</strong> <span className="small">â€¢ {new Date(p.createdAt).toLocaleString()}</span></div>
            <div style={{ marginTop:6 }}>{p.content}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
